import { Component } from '@angular/core';
import { FormGroup, FormControl,FormBuilder,Validators } from '@angular/forms';
import {UserService} from '../../services/user.service'

@Component({
  selector: 'app-password',
  templateUrl: './password.component.html',
  styleUrls: ['./password.component.css']
})
export class PasswordComponent {

  userForm:FormGroup;
constructor(private fb: FormBuilder,private srvc:UserService) { 


this.userForm = this.fb.group({
  password: ['', [Validators.required]],
  rpassword: ['', [Validators.required]],



});

}

 onSubmit() {
if(this.userForm.value.password==this.userForm.value.rpassword){ 
this.srvc.chgPwd(this.userForm.value).subscribe(result=>{
  this.msg=result.response;
  if(result.st==1)
    this.userForm.reset();
  })}
else{
this.msg="retype password not matched";
}
} 

}
<div class="container pt-3 pb-3">
<div class="row justify-content-center">
<div class="col-6 bg-white p-5 shadow mt-5 mb-2">
<p class="text-success display-6 text-center mb-4">Change Your Password</p>
<form [formGroup]="userForm" (ngSubmit)="onSubmit()">
<div class="mb-3">
<input formControlName="password" type="text" placeholder="Enter Your Password" class="form-control">
</div>
<div class="mb-3">
<input formControlName="rpassword" type="text" placeholder="Re-enter Your Password" class="form-control">
</div>

<div class="mb-3">
<button class="btn btn-outline-success btn-lg w-100 mt-3" [disabled]="!userForm.valid">Update</button>

</div>
</form>
<div class="bg-light border p-3" ><p class="text-warning fw-bold">{{msg}}</p></div>

</div>
</div>
</div>
