import {Component } from '@angular/core';
import{CatgeoryService} from '../../services/catgeory.service'
import{ArticleService} from '../../services/article.service'
import { FormGroup, FormControl,FormBuilder,Validators } from '@angular/forms';
import { AngularEditorConfig } from '@kolkov/angular-editor';

@Component({
  selector: 'app-addarticle',
  templateUrl: './addarticle.component.html',
  styleUrls: ['./addarticle.component.css']
})

export class AddarticleComponent {
data:any;

msg=""
articleForm:FormGroup;

editorConfig: AngularEditorConfig = {
    editable: true,
      spellcheck: true,
      height: 'auto',
      minHeight: '0',
      maxHeight: 'auto',
      width: 'auto',
      minWidth: '0',
      translate: 'yes',
      enableToolbar: true,
      showToolbar: true,
      placeholder: 'Enter text here...',
      defaultParagraphSeparator: '',
      defaultFontName: '',
      defaultFontSize: '',
      fonts: [
        {class: 'arial', name: 'Arial'},
        {class: 'times-new-roman', name: 'Times New Roman'},
        {class: 'calibri', name: 'Calibri'},
        {class: 'comic-sans-ms', name: 'Comic Sans MS'}
      ],

    toolbarPosition: 'top',
    toolbarHiddenButtons: [
      [],
      []
    ]
};




constructor(private fb: FormBuilder,private srvc:CatgeoryService,private asrvc:ArticleService) { 

this.articleForm = this.fb.group({
  title: ['', [Validators.required]],
  image: [''],
  content: ['', [Validators.required]],
  category: ['', [Validators.required]],
});


this.srvc.getCategory().subscribe(result=>{this.data=result})
}


onSubmit() {
this.asrvc.saveArticle(this.articleForm.value.title,this.articleForm.value.content,this.articleForm.value.category,
  this.articleForm.value.image).subscribe(result=>{
this.msg=result.response;
if(result.st==1)
{
  this.articleForm.reset();
}
})



} 

onFileSelect(event:any){
    console.log((event.target as HTMLInputElement).files)
    const file = (event.target as HTMLInputElement).files?.[0];
    this.articleForm.patchValue({image:file });
    this.articleForm.get('image')?.updateValueAndValidity()
  }

}
<div class="container pt-5">
<div class="row justify-content-center">
<div class="col-10">
<h3>Add New Article</h3>
<form [formGroup]="articleForm" (ngSubmit)="onSubmit()">
<div class="mb-3">
	<label>Post Title</label>
<input formControlName="title" type="text" placeholder="Enter Article Title" class="form-control">
</div>
<div class="row">
<div class="mb-3 col-6">
	<label>Choose Image</label>
<input (change)="onFileSelect($event)" type="file" class="form-control">
</div>
<div class="mb-3 col-6">
	<label>Select Category</label>
<select formControlName="category" class="form-control">
	<option *ngFor="let d of data">{{d.title}}</option>
</select>
</div>
</div>
<div class="mb-3">
	<label>Content</label>
<angular-editor formControlName="content" [config]="editorConfig"></angular-editor>
</div>

<div class="mb-3">
<button class="btn btn-outline-success btn-lg w-100 mt-3" [disabled]="!articleForm.valid">Save Article</button>
</div>
</form>
<div class="bg-light border p-3" *ngIf="msg!=''"><p class="text-warning fw-bold">{{msg}}</p></div>

</div>
</div>
</div>
