import {Component } from '@angular/core';
import{CatgeoryService} from '../../services/catgeory.service'
import { FormGroup, FormControl,FormBuilder,Validators } from '@angular/forms';

@Component({
  selector: 'app-addarticle',
  templateUrl: './addarticle.component.html',
  styleUrls: ['./addarticle.component.css']
})

export class AddarticleComponent {
data:any;
dmsg:string="";

msg=""
articleForm:FormGroup;

constructor(private fb: FormBuilder,private srvc:CatgeoryService) { 

this.articleForm = this.fb.group({
  title: ['', [Validators.required]],
  image: [''],

});


this.srvc.getCategory().subscribe(result=>{this.data=result})
}

del(id:string){
this.srvc.deleteCategory(id).subscribe(result=>{
this.dmsg=result.response;
if(result.st==1)
this.srvc.getCategory().subscribe(result=>{this.data=result})
})
}

onSubmit() {
this.srvc.saveCategory(this.articleForm.value.title,this.articleForm.value.image).subscribe(result=>{
this.msg=result.response;
if(result.st==1)
{
  this.articleForm.reset();

this.srvc.getCategory().subscribe(result=>{this.data=result})
}
})



} 

onFileSelect(event:any){
    console.log((event.target as HTMLInputElement).files)
    const file = (event.target as HTMLInputElement).files?.[0];
    this.articleForm.patchValue({image:file });
    this.articleForm.get('image')?.updateValueAndValidity()
  }

}
